(function(){function S(e,t){this.data=e;this.ctx=t;this.draw()}S.prototype.draw=function(){var d=this.data;if(!d||!d.data){return;}var ctx=this.ctx;var total=0;for(var i=0;i<d.data.length;i++){total+=d.data[i];}
var start=0;var colors=d.colors||['#dc2626','#2563eb','#16a34a','#f59e0b'];for(i=0;i<d.data.length;i++){var val=d.data[i];var slice=2*Math.PI*(val/total);ctx.beginPath();ctx.moveTo(100,100);ctx.fillStyle=colors[i%colors.length];ctx.arc(100,100,100,start,start+slice);ctx.closePath();ctx.fill();start+=slice;}if(d.legend){ctx.font='12px sans-serif';ctx.textBaseline='top';var y=210;for(i=0;i<d.labels.length;i++){ctx.fillStyle=colors[i%colors.length];ctx.fillRect(10,y,10,10);ctx.fillStyle='#111';ctx.fillText(d.labels[i]+': '+d.data[i],25,y);y+=14;}}};window.SimplePie=S;})();